$(function () {
  $(window).scroll(function () {
    let scrollTop = $(window).scrollTop();
    console.log(scrollTop);

    if (scrollTop > 300) {
      $('header').addClass('on');
    } else {
      $('header').removeClass('on');
    }

    let topScrollTop = $('#top').offset().top;
    if (topScrollTop > 1000) {
      $('#top').addClass('on');
    } else {
      $('#top').removeClass('on');
    }
  });
});
