$(function () {
  $(window).scroll(function () {
    let scrollTop = $(window).scrollTop();
    console.log(scrollTop);

    if (scrollTop > 300) {
      $('header').addClass('on');
      $('#top').addClass('on');
    } else {
      $('header').removeClass('on');
      $('#top').removeClass('on');
    }

    let weare = $('#weare').offset().top;
    let work = $('#work').offset().top;

    // console.log('weare : ' + weare);
    // console.log('work : ' + work);

    // if (scrollTop < 10) {
    //   $('#weare').removeClass('on');
    // } else if (scrollTop > weare - 400) {
    //   $('#weare').addClass('on');
    // } else {
    //   $('#weare').removeClass('on');
    // }

    // if (scrollTop > work - 400) {
    //   $('#work').addClass('on');
    // } else {
    //   $('#work').removeClass('on');
    // }

    $('#content > section').each(function (idx, value) {
      let offsetTop = $(this).offset().top;
      console.log(idx, value.offsetTop);
      if (scrollTop < 10) {
        $(this).removeClass('on');
      } else if (scrollTop > offsetTop - 400) {
        $(this).addClass('on');
      } else {
        $(this).removeClass('on');
      }
    });
  });

  $('.toggle').click(function () {
    $('header nav').toggleClass('on');
  });
});
